declare @employeeId int;
declare @roleId int;
declare @subAccountId int = (select top 1 subaccountId from accountsSubAccounts);

begin try
	select @employeeId = employeeid from employees where username = 'mobileDevice_unitTestUser';

	insert into accessRoles (rolename, roleAccessLevel) values ('mobileDevice_UTAccessRole', 1);
	set @roleId = scope_identity();

	if @roleId > 0
		select 1;
	else
		select 0;

	insert into accessRoleElementDetails (roleid, elementid, viewAccess) values (@roleId, 14, 1);

	select roleId from accessRoleElementDetails where roleid = @roleId and elementId = 14;
	
	insert into employeeAccessRoles (employeeId, accessRoleId, subAccountId) values (@employeeId, @roleId, @subAccountId);

	select employeeId from employeeAccessRoles where employeeId = @employeeId and accessRoleId = @roleId;
end try
begin catch
	select -999;
end catch

